def topla (a,b):      return a+bdef çıkar (a,b):    return a-bdef çarp(a,b):        return a*bdef böl (a,b):        return a/bwhile True:            a= float(input("İşlem yapmak istediğiniz 1.sayıyı giriniz"))          islem=input("yapmak istediğimiz işlemi seçin [+] [-] [/] [*] ")        b= float(input("İşlem yapmak istediğiniz 2.sayıyı giriniz"))                    if (islem == "+"):                input(topla(a,b))            if (islem == "-"):                input(çıkar(a,b))            if (islem == "*"):                input(çarp(a,b))            if (islem == "/"):                input(böl(a,b))